mapper.update:
update ums_role
role_name = #{roleName,jdbcType=VARCHAR}, description = #{description ,jdbcType=VARCHAR}, available=#{available,jdbcType=VARCHAR}, admin_count=#{adminCount,jdbcType=VARCHAR}, sort=#{sort,jdbcType=VARCHAR},
where role_id = #{roleId,jdbcType=VARCHAR} and sys_id = #{sysId,jdbcType=VARCHAR}
